import LocalStorageHelper from '../helpers/localStorage.helper';
import './app.css';
/**
 * Controller for the application
 */
class StorageAppController {
  keyInput: any;
  valInput: any;

  /**
   * Set up the app, generate html and set up event handlers
   */
  constructor() {
    // TODO: Set up HTML from Template?
    this.keyInput = document.getElementById('key-input');
    this.valInput = document.getElementById('value-input');

    document.getElementsByClassName('save-btn')[0].onclick = this.save.bind(this);
    document.getElementsByClassName('get-btn')[0].onclick = this.view.bind(this);

    document.getElementsByClassName('clear-value-btn')[0].onclick = this.clearValue.bind(this);
  }

  /**
   * saves the value at the provided key
   * @return {void}
   */
  save() {
    // Get key and value from ui
    const key = this.keyInput.value;
    const val = this.valInput.innerText;

    // save with local storage
    LocalStorageHelper.save({ key, val });

    // TODO: display message to user
  }

  /**
   * retrieves a value using the provided key
   * @return {String} value retrieved from local storage with key
   */
  view() {
    // get key from ui
    const key = this.keyInput.value;

    // get from local storage
    const val = LocalStorageHelper.get({ key });

    // display value to user
    this.valInput.innerText = val;
  }

  /**
   * Clear the value input element of text
   * @return {void}
   */
  clearValue() {
    this.valInput.innerText = '';
  }
}

export default StorageAppController;

/**
 * @flow
 */
import LocalStorageHelper from './localStorage.helper';

describe('LocalStorageService', function() {

  describe('#save', function() {

    it('should use local storage to save value with key', function() {
      sandbox.spy(localStorage, 'setItem');
      const key = 'test';
      const val = 'val';

      LocalStorageHelper.save({
        key: 'test',
        val: 'val',
      });

      expect(localStorage.setItem).to.be.called;
      expect(localStorage.setItem.args[0][0]).to.equal(key);
      expect(localStorage.setItem.args[0][1]).to.equal(val);
    });

    it('should stringify objects before saving them', function() {
      sandbox.spy(localStorage, 'setItem');

      const val = {
        test: 123
      };
      LocalStorageHelper.save({
        key: 'abc',
        val
      });

      const stringified = JSON.stringify(val);
      expect(localStorage.setItem.args[0]).to.include(stringified);
    });

    it('should be able to retrieve value using key with #get', function() {
      const val = 'test';
      const key = 'abc';

      LocalStorageHelper.save({
        key,
        val
      });

      const saved = LocalStorageHelper.get({
        key
      });

      expect(saved).to.equal(val);
    });
  });

  describe('#get', function() {

    it('should retrieve value from local storage using key', function() {
      sandbox.stub(localStorage, 'getItem').returns('abc');
      const key = 'test';

      LocalStorageHelper.get({
        key
      });

      expect(localStorage.getItem).to.be.called;
      expect(localStorage.getItem.args[0][0]).to.equal(key);
    });

    it('should return object if object stored', function() {
      const key = 'abc';
      const val = {
        test: 123
      };
      LocalStorageHelper.save({
        key,
        val
      });

      const returned = LocalStorageHelper.get({ key });
      expect(returned).to.deep.equal(val);
    });

    it('should throw error if requesting key with no value assigned', function() {
      const key = 'unused_123_abc';

      expect(() => {
        LocalStorageHelper.get({ key });
      }).to.throw();
    });
  });
});

